import { Component, OnInit } from '@angular/core';
import { AuthService } from './auth.service'; 
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  constructor(public authService: AuthService, private router: Router) {}

  logout() {
    this.authService.logout(); 
    this.router.navigate(['/login']);
  }
  ngOnInit() {
    console.log('Is Logged:', this.authService.isLogged);
}
}
<header>
  <h1>Welcome to Wonders and Beyond!</h1>
</header>

<!-- Navigation bar for NOT logged-in users -->
<div class="navbar" *ngIf="!authService.isLogged">
  <a routerLink="/login">Login</a>
  <a routerLink="/register">Register</a>
</div>

<!-- Navigation bar for logged-in users -->
<div class="navbar">
  <a routerLink="/home">Home</a>
  <a routerLink="/deities">Deities</a>
  <a routerLink="/potions">Potions</a>
  <a routerLink="/spells">Spells</a>
  <a routerLink="/beyonds">Beyonds</a>
  <a routerLink="/findings">Search</a>
  <a (click)="logout()">Logout</a>
</div>

<main>
  <router-outlet></router-outlet>
</main>
