import { Component } from '@angular/core';
import { AuthService } from './auth.service'; 
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  constructor(public authService: AuthService, private router: Router) {}

  logout() {
    this.authService.logout(); 
    this.router.navigate(['/login']);
  }
}
<header>
  <h1>Welcome to Wonders and Beyond!</h1>
</header>

<div class="navbar" *ngIf="authService.isLoggedIn(); else notLoggedIn">
  <a routerLink="/home">Home</a>
  <a routerLink="/deities">Deities</a>
  <a routerLink="/potions">Potions</a>
  <a routerLink="/spells">Spells</a>
  <a routerLink="/beyonds">Beyonds</a>
  <a routerLink="/findings">Search</a>
  <a (click)="logout()">Logout</a>
</div>

<ng-template #notLoggedIn>
  <div class="navbar">
    <a routerLink="/login">Login</a>
    <a routerLink="/">or</a>
    <a routerLink="/register">Register</a>
  </div>
</ng-template>

<main>
  <router-outlet></router-outlet>
</main>
