import { Component, OnInit } from '@angular/core';
import { PotionsService } from './potions.service'; // Update the path

@Component({
  selector: 'app-potions',
  templateUrl: './potions.component.html',
  styleUrls: ['./potions.component.css']
})
export class PotionsComponent implements OnInit {
  potions: any[] = [];

  constructor(private potionsService: PotionsService) {}

  ngOnInit(): void {
    this.potionsService.getPotions().subscribe(
      data => {
        this.potions = data;
      },
      err => {
        console.error(err);
        // You can handle errors here, perhaps show a message to the user
      }
    );
  }
}
