import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface SpellShare {
  username: string;
  post: string;
}

@Injectable({
  providedIn: 'root'
})
export class SpellShareService {
  private apiUrl = 'http://localhost:3000/api/spellShares'; // Corrected the API URL
  
  constructor(private http: HttpClient) {}

  getAllSpellShares(): Observable<SpellShare[]> {
    return this.http.get<SpellShare[]>(this.apiUrl);
  }

  createSpellShare(spellShare: SpellShare): Observable<SpellShare> {
    return this.http.post<SpellShare>(this.apiUrl, spellShare);
  }
}
