import { Component, OnInit } from '@angular/core';
import { BeyondsService } from '../services/beyonds.service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-beyonds',
  templateUrl: './beyonds.component.html',
  styleUrls: ['./beyonds.component.css']
})
export class BeyondsComponent implements OnInit {
  beyonds: any[] = [];
  title = '';   
  post = '';
  author = '';  
  searchTerm = ''; 

  constructor(private beyondsService: BeyondsService) { }

  ngOnInit(): void {
    this.loadBeyonds();
  }

  loadBeyonds(): void {
    this.beyondsService.getAllBeyonds().subscribe(
      data => this.beyonds = data,
      error => console.error(error)
    );
  }

  onSubmit(): void {
    if (this.title.trim() === '') {
      console.log('Title cannot be blank');
      return;
    }

    const newBeyond = {
      title: this.title,
      post: this.post,
      author: this.author || 'Anonymous'  
    };

    this.beyondsService.createBeyond(newBeyond).subscribe(
      data => {
        this.beyonds.push(data);
        this.title = ''; 
        this.author = '';
        this.post = '';
      },
      (error) => {
        console.error('Post failed', error);
      }
    );
  }

  onDelete(id: string): void {
    this.beyondsService.deleteBeyonds(id).subscribe(
      () => this.beyonds = this.beyonds.filter(beyond => beyond._id !== id),
      error => console.error(error)
    );
  }

  updatePost(beyond: any): void {
    this.beyondsService.updateBeyond(beyond._id, beyond).subscribe(
      (updatedBeyond) => {
        const index = this.beyonds.findIndex(b => b._id === updatedBeyond._id);
        this.beyonds[index] = updatedBeyond;
        beyond.isEditing = false; 
      },
      (error) => {
        console.error('Update failed', error);
        beyond.isEditing = false;
      }
    );
  }

  searchPosts(): void {
    if (this.searchTerm.trim() === '') {
      this.loadBeyonds();
      return;
    }

    this.beyondsService.searchBeyonds(this.searchTerm).subscribe(
      data => {
        this.beyonds = data;
        if (this.beyonds && this.beyonds.length === 0) {
          console.log('Data not present');
        }
      },
      error => {
        console.error('Error performing search', error);
        this.beyonds = [];
      }
    );
  }
}
<div class="container">
  <div class="form-container">
    <form (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="title">Title:</label>
        <input type="text" id="title" name="title" [(ngModel)]="postForm.title" required>
      </div>
      <div class="form-group">
        <label for="post">Post:</label>
        <textarea id="post" name="post" [(ngModel)]="postForm.post" required></textarea>
      </div>
      <div class="form-group">
        <label for="author">Author (optional):</label>
        <input type="text" id="author" name="author" [(ngModel)]="postForm.author">
      </div>
      <button type="submit">Post</button>
    </form>
  </div>

  <div class="form-container">
    <input type="text" [(ngModel)]="searchTerm" (change)="searchPosts()" placeholder="Search posts...">
    <button (click)="searchPosts()">Search</button>
  </div>

  <div id="beyondsPosts">
    <ng-container *ngIf="beyonds.length; else noPosts">
      <div *ngFor="let beyond of beyonds; let i = index" class="post-container">
        <ng-container *ngIf="!beyond.isEditing">
          <h3>{{ beyond.title }}</h3>
          <p>{{ beyond.post }}</p>
          <p><em>Posted by: {{ beyond.author || 'Anonymous' }}</em></p>
          <button (click)="onDelete(beyond._id)">Delete</button>
          <span class="btn-spacing"></span>
          <button (click)="editPost(beyond)">Edit</button>
        </ng-container>

        <ng-container *ngIf="beyond.isEditing">
          <form (ngSubmit)="updatePost(beyond)">
            <div class="form-group">
              <label for="editTitle">Title:</label>
              <input type="text" id="editTitle" name="title" [(ngModel)]="beyond.title" required>
            </div>
            <div class="form-group">
              <label for="editPost">Post:</label>
              <textarea id="editPost" name="post" [(ngModel)]="beyond.post" required></textarea>
            </div>
            <div class="form-group">
              <label for="editAuthor">Author (optional):</label>
              <input type="text" id="editAuthor" name="author" [(ngModel)]="beyond.author">
            </div>
            <button type="submit">Save</button>
            <span class="btn-spacing"></span>
            <button (click)="cancelEdit(beyond)">Cancel</button>
          </form>
        </ng-container>
        <hr>
      </div>
    </ng-container>
    <ng-template #noPosts>
      <div class="post-container">
        <p>No posts available.</p>
      </div>
    </ng-template>
  </div>
</div>
