import { Component, OnInit } from '@angular/core';
import { SpellShareService, SpellShare } from '../services/spellShare.service'; 

@Component({
  selector: 'app-spellShare',
  templateUrl: './spellShare.component.html',
  styleUrls: ['./spellShare.component.css']
})
export class SpellShareComponent implements OnInit {
  newSpellShare: SpellShare = {
    username: '',
    post: ''
  };
  spellShare: SpellShare[] = [];

  constructor(private spellShareService: SpellShareService) { }

  ngOnInit(): void {
    this.loadSpellShare();
  }

  submitSpellShare(): void {
    this.spellShareService.createSpellShare(this.newSpellShare).subscribe(data => {
      this.loadSpellShare();
      this.newSpellShare.username = '';
      this.newSpellShare.post = '';
    });
  }

  loadSpellShare(): void {
    this.spellShareService.getAllSpellShare().subscribe(data => {
      this.spellShare = data;
    });
  }
}
<div class="spell-share-container">
  <div class="create-spell-share">
    <h2>Share your Spell Research</h2>
    <div>
      <label for="username">Username:</label>
      <input type="text" id="username" [(ngModel)]="newSpellShare.username">
    </div>
    <div>
      <label for="post">Post:</label>
      <textarea id="post" rows="4" [(ngModel)]="newSpellShare.post"></textarea>
    </div>
    <button (click)="submitSpellShare()">Share</button>
  </div>

  <div class="spell-shares">
    <h2>Community Spell Shares</h2>
    <ul>
      <li *ngFor="let spellShare of spellShares">
        <strong>{{ spellShare.username }}:</strong> {{ spellShare.post }}
      </li>
    </ul>
  </div>
</div>
