import { Component, OnInit } from '@angular/core';
import { BeyondsService } from '../services/beyonds.service';

@Component({
  selector: 'app-beyonds',
  templateUrl: './beyonds.component.html',
  styleUrls: ['./beyonds.component.css']
})
export class BeyondsComponent implements OnInit {
  beyonds: any[] = [];
  title = '';
  post = '';
  author = '';
  searchTerm = '';

  constructor(private beyondsService: BeyondsService) {}

  ngOnInit(): void {
    this.loadBeyonds();
  }

  loadBeyonds(): void {
    this.beyondsService.getAllBeyonds().subscribe(
      (data: any[]) => this.beyonds = data,
      (error) => console.error(error)
    );
  }

  onSubmit(): void {
    if (!this.title.trim()) {
      console.log('Title cannot be blank');
      return;
    }

    const newBeyond = {
      title: this.title,
      post: this.post,
      author: this.author || 'Anonymous'
    };

    this.beyondsService.createBeyond(newBeyond).subscribe(
      (data: any) => {
        this.beyonds.push(data);
        this.title = '';
        this.author = '';
        this.post = '';
      },
      (error) => {
        console.error('Post failed', error);
      }
    );
  }

  onDelete(id: string): void {
    this.beyondsService.deleteBeyonds(id).subscribe(
      () => this.beyonds = this.beyonds.filter((beyond) => beyond._id !== id),
      (error) => console.error(error)
    );
  }

  updatePost(beyond: any): void {
    this.beyondsService.updateBeyond(beyond._id, beyond).subscribe(
      (updatedBeyond: any) => {
        const index = this.beyonds.findIndex((b) => b._id === updatedBeyond._id);
        this.beyonds[index] = updatedBeyond;
        beyond.isEditing = false;
      },
      (error) => {
        console.error('Update failed', error);
        beyond.isEditing = false;
      }
    );
  }

  searchPosts(): void {
    if (!this.searchTerm.trim()) {
      this.loadBeyonds();
      return;
    }

    this.beyondsService.searchBeyonds(this.searchTerm).subscribe(
      (data: any[]) => {
        this.beyonds = data;
        if (!this.beyonds || this.beyonds.length === 0) {
          console.log('Data not present');
        }
      },
      (error) => {
        console.error('Error performing search', error);
        this.beyonds = [];
      }
    );
  }
}
<div class="container">
  <div class="form-container">
    <form (ngSubmit)="onSubmit()" #postForm="ngForm">
      <div class="form-group">
        <label for="title">Title:</label>
        <input type="text" id="title" [(ngModel)]="title" name="title" required>
      </div>
      <div class="form-group">
        <label for="post">Post:</label>
        <textarea id="post" [(ngModel)]="post" name="post" required></textarea>
      </div>
      <div class="form-group">
        <label for="author">Author (optional):</label>
        <input type="text" id="author" [(ngModel)]="author" name="author">
      </div>
      <button class="btn" type="submit" [disabled]="postForm.invalid">Post</button>
    </form>
  </div>

  <div class="form-container">
    <input type="text" [(ngModel)]="searchTerm" (change)="searchPosts()" placeholder="Search posts...">
    <button class="btn" (click)="searchPosts()">Search</button>
  </div>

  <div id="beyondsPosts">
    <ng-container *ngIf="beyonds.length; else noPosts">
      <div *ngFor="let beyond of beyonds; let i = index" class="post-container">
        <div *ngIf="!beyond.isEditing && beyond.title?.trim() && beyond.post?.trim()">
          <h3>{{ beyond.title }}</h3>
          <p>{{ beyond.post }}</p>
          <p><em>Posted by: {{ beyond.author || 'Anonymous' }}</em></p>
          <button class="btn" (click)="onDelete(beyond._id)">Delete</button>
          <button class="btn" (click)="beyond.isEditing = true">Edit</button>
        </div>

        <div *ngIf="beyond.isEditing">
          <div class="form-group">
            <label [for]="'title' + i">Title:</label>
            <input type="text" [id]="'title' + i" [(ngModel)]="beyond.title" name="title" required>
          </div>
          <div class="form-group">
            <label [for]="'post' + i">Post:</label>
            <textarea [id]="'post' + i" [(ngModel)]="beyond.post" name="post" required></textarea>
          </div>
          <div class="form-group">
            <label [for]="'author' + i">Author (optional):</label>
            <input type="text" [id]="'author' + i" [(ngModel)]="beyond.author" name="author">
          </div>
          <button class="btn" (click)="updatePost(beyond)">Save</button>
          <button class="btn" (click)="beyond.isEditing = false">Cancel</button>
        </div>
        </div>
    </ng-container>
    <ng-template #noPosts>
      <p>No posts found.</p>
    </ng-template>
  </div>
</div>
