import { Component, OnInit } from '@angular/core';
import { BeyondsService } from '../services/beyonds.service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-beyonds',
  templateUrl: './beyonds.component.html',
  styleUrls: ['./beyonds.component.css']
})
export class BeyondsComponent implements OnInit {
  beyonds: any[] = [];
  title = '';   
  post = '';
  author = '';  
  searchTerm = ''; 

  constructor(private beyondsService: BeyondsService) { }

  ngOnInit(): void {
    this.loadBeyonds();
  }

  loadBeyonds(): void {
    this.beyondsService.getAllBeyonds().subscribe(
      data => this.beyonds = data,
      error => console.error(error)
    );
  }

  onSubmit(): void {
    const newBeyond = {
      title: this.title,
      post: this.post,
      author: this.author || 'Anonymous'  
    };

    this.beyondsService.createBeyond(newBeyond).subscribe(
      data => {
        this.beyonds.push(data);
        this.title = ''; 
        this.author = '';
        this.post = '';
      },
      (error) => {
        console.error('Post failed', error);
      }
    );
  }

  onDelete(id: string): void {
    this.beyondsService.deleteBeyonds(id).subscribe(
      () => this.beyonds = this.beyonds.filter(beyond => beyond._id !== id),
      error => console.error(error)
    );
  }

  updatePost(beyond: any): void {
    this.beyondsService.updateBeyond(beyond._id, beyond).subscribe(
      (updatedBeyond) => {
        const index = this.beyonds.findIndex(b => b._id === updatedBeyond._id);
        this.beyonds[index] = updatedBeyond;
        beyond.isEditing = false; 
      },
      (error) => {
        console.error('Update failed', error);
        beyond.isEditing = false;
      }
    );
  }

  onSearch(): void {
    this.searchPosts();
  }

  searchPosts(): void {
    if (this.searchTerm.trim() === '') {
      this.loadBeyonds();
      return;
    }

    // Use a similar GET request as the second component
    this.http.get<any[]>(`http://localhost:3000/api/search?q=${this.searchTerm}`).subscribe(
      data => {
        this.beyonds = data;
        if (this.beyonds && this.beyonds.length === 0) {
          console.log('Data not present');
        }
      },
      error => {
        console.error('Error performing search', error);
        this.beyonds = [];
      }
    );
  }

}
<div>
  <br>
  <form (ngSubmit)="onSubmit()">
    <div>
      <label for="title">Title:</label>
      <input type="text" id="title" name="title" [(ngModel)]="title" required>
    </div>
    <div>
      <label for="post">Post:</label>
      <textarea id="post" name="post" [(ngModel)]="post" required></textarea>
    </div>
    <div>
      <label for="author">Author (optional):</label>
      <input type="text" id="author" name="author" [(ngModel)]="author">
    </div>
    <button type="submit">Post</button>
  </form>

  <input type="text" [(ngModel)]="searchTerm" (change)="onSearch()" placeholder="Search posts...">
  <button (click)="onSearch()">Search</button>

  <div id="beyondsPosts">
    <ng-container *ngIf="beyonds.length; else noPosts">
      <div *ngFor="let beyond of beyonds; let i = index">
        <div *ngIf="!beyond.isEditing">
          <h3>{{ beyond.title }}</h3>
          <p>{{ beyond.post }}</p>
          <p><em>Posted by: {{ beyond.author || 'Anonymous' }}</em></p>
          <button (click)="onDelete(beyond._id)">Delete</button>
          <span style="margin-right: 10px;"></span>
          <button (click)="beyond.isEditing = true">Edit</button>
        </div>

        <div *ngIf="beyond.isEditing">
          <div>
            <label [for]="'title' + i">Title:</label>
            <input type="text" [id]="'title' + i" name="title" [(ngModel)]="beyond.title" required>
          </div>
          <div>
            <label [for]="'post' + i">Post:</label>
            <textarea [id]="'post' + i" name="post" [(ngModel)]="beyond.post" required></textarea>
          </div>
          <div>
            <label [for]="'author' + i">Author (optional):</label>
            <input type="text" [id]="'author' + i" name="author" [(ngModel)]="beyond.author">
          </div>
          <button (click)="updatePost(beyond)">Save</button>
          <span style="margin-right: 10px;"></span>
          <button (click)="beyond.isEditing = false">Cancel</button>
        </div>
        <hr>
      </div>
    </ng-container>
    <ng-template #noPosts>
      <p>No posts available.</p>
    </ng-template>
  </div>
</div>
