import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { tap } from 'rxjs/operators';


interface LoginResponse {
  success: boolean;
  token?: string;  // Include other properties you expect in the response if necessary.
  message?: string;
}

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  private isAuthenticated = false;
  private baseUrl = 'http://localhost:3000';

  constructor(private http: HttpClient) {
    this.isAuthenticated = !!localStorage.getItem('isAuthenticated');
  }

  register(user: any): Observable<any> {
    return this.http.post(`${this.baseUrl}/api/users/register`, user);
  }

  authenticate(username: string, password: string): Observable<LoginResponse> {
    const url = `${this.baseUrl}/api/users/login`;
    return this.http.post<LoginResponse>(url, { username, password }).pipe(
      tap((response) => {
        if (response && response.success) {
          this.isAuthenticated = true;
          localStorage.setItem('isAuthenticated', 'true');
        } else {
          this.isAuthenticated = false;
          localStorage.removeItem('isAuthenticated');
        }
      })
    );
  }

  logout(): void {
    this.isAuthenticated = false;
    localStorage.removeItem('isAuthenticated');
  }

  isLoggedIn(): boolean {
    return this.isAuthenticated;
  }
}
