import { Component } from '@angular/core';

@Component({
  selector: 'app-potions',
  templateUrl: './potions.component.html',
  styleUrls: ['./potions.component.css']
})
export class PotionsComponent {
  potions = [
    { name: 'Healing Potion', description: 'Restores health.' },
    { name: 'Invisibility Elixir', description: 'Makes the drinker invisible.' },
    // Add more potions
  ];
}
