import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service'; // Import the AuthService

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  username: string = '';
  email: string = '';
  password: string = '';
  firstName: string = '';
  lastName: string = '';

  constructor(
    private router: Router,
    private authService: AuthService // Inject the AuthService
  ) {}

  isValidPassword(password: string): boolean {
    const regex = /^(?=.*[A-Z])(?=.*\d)[A-Za-z\d]{6,}$/;
    return regex.test(password);
  }


  onSubmit() {
    if (!this.username.trim()) {
      alert('Username cannot be blank!');
      return;
    }
    if (!this.email.trim()) {
      alert('Email cannot be blank!');
      return;
    }
    if (!this.password.trim()) {
      alert('Password cannot be blank!');
      return;
    }
    if (!this.firstName.trim()) {
      alert('First Name cannot be blank!');
      return;
    }
    if (!this.lastName.trim()) {
      alert('Last Name cannot be blank!');
      return;
    }
    if (!this.password.trim()) {
      alert('Password cannot be blank!');
      return;
    }
    if (!this.isValidPassword(this.password)) {
      alert('Password must be at least 6 characters long, contain at least one uppercase letter, and one number.');
      return;
    }

    const user = {
      username: this.username,
      email: this.email,
      password: this.password,
      firstName: this.firstName,
      lastName: this.lastName
    };

    // Use the authService to register the user
    this.authService.register(user).subscribe(
      response => {
        console.log('User registered successfully', response);
        this.router.navigate(['/login']);
      },
      error => {
        console.error('Registration failed:', error);
        // Optionally, you can add detailed error messages based on the error returned from the API.
      }
    );
  }
}
