import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable} from 'rxjs';
import { tap} from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  private isAuthenticated = false; // Initially, the user is not authenticated
  private baseUrl = 'http://localhost:3000'; // Base URL for the server

  constructor(private http: HttpClient) {}

  authenticate(username: string, password: string): Observable<any> {
    const url = `${this.baseUrl}/api/users/login`;
    return this.http.post(url, { username, password }).pipe(
      tap((response) => {
        this.isAuthenticated = true; // set isAuthenticated to true after successful login
      })
    );
  }

  isLoggedIn(): boolean {
    return this.isAuthenticated; // Return the authentication status
  }
}
